mapper.insert:
INSERT INTO purchase_req_detail (
  pch_code,
  item_code,
  item_qty
) VALUES (
  #{pch_code},
  #{item_code},
  #{item_qty}
)

mapper.select:
SELECT * FROM ( SELECT inner_result.*, ROWNUM rnum FROM ( SELECT pch.pidx, pch.pch_code, pch.due_date, pch.pch_status, pch.request_date, pch.pay_amount, cmp.company_code, cmp.company_name, cmp.manager_name FROM purchase_req pch LEFT JOIN client_comp cmp ON pch.company_code = cmp.company_code WHERE 1=1
AND ( cmp.company_name LIKE '%' || #{search_word} || '%' OR cmp.manager_name LIKE '%' || #{search_word} || '%' )
AND pch.pch_status IN
#{status}
ORDER BY pch.pidx DESC ) inner_result WHERE ROWNUM < #{end} ) WHERE rnum > #{start}

mapper.update:
UPDATE purchase_req_detail
SET
	item_qty = #{item_qty}
WHERE pch_code = #{pch_code}
  AND item_code = #{item_code}

UPDATE purchase_req
SET pch_status = #{pch_status}
WHERE pch_code = #{pch_code}
