mapper.insert:
INSERT INTO purchase_req_detail (
  pch_code,
  item_code,
  item_qty
) VALUES (
  #{pch_code},
  #{item_code},
  #{item_qty}
)

mapper.select:
SELECT * FROM ( SELECT inner_result.*, ROWNUM rnum FROM ( SELECT prh.pidx, prh.pch_code, prh.due_date, prh.pch_status, prh.request_date, prh.pay_amount, cmp.company_name, mgr.manager_name FROM purchase_req_header prh JOIN temp_client_comp cmp ON prh.company_code = cmp.company_code JOIN temp_client_manager mgr ON cmp.company_code = mgr.company_code WHERE 1=1
AND ( cmp.company_name LIKE '%' || #{sword} || '%' OR mgr.manager_name LIKE '%' || #{sword} || '%' )
AND prh.pch_status IN
#{status}
ORDER BY prh.pidx DESC ) inner_result WHERE ROWNUM < #{end} ) WHERE rnum > #{start}
